import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import React, {useState} from 'react'
import axios from 'axios';
import HeaderComponent from '../HeaderComponent';
import { useHistory, useLocation } from 'react-router-dom';
import {location} from 'react-router-dom'
import { Container } from '@material-ui/core';

const EMPLOYEE_API_BASE_URL = "http://localhost:8080/api/v1/employee";
const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));
function EditEmployee(props) {
    const history = useHistory();
    const classes = useStyles();
    const [employeeState, setEmployeeState] = useState({});
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [salary, setSalary] = useState('');

    const getEmployeeById=(id)=>{
        axios.get(EMPLOYEE_API_BASE_URL+'/'+id).then(data=>{
            let employee = data.data;
            setEmployeeState({
              id: employee.id,
              firstName:employee.firstName,
              secondName:employee.secondName,
              salary:employee.salary  
            });
            setFirstName(employee.firstName);
            setSecondName(employee.secondName);
        })

    }
    React.useEffect(() => {
        let id = props.match.params.id;
        getEmployeeById(id);
      }, []);
    const acceptClick=()=>{
        const employee = {
            firstName: firstName,
            secondName: secondName,
            salary: salary
        }

        axios.put(EMPLOYEE_API_BASE_URL + '/' + employeeState.id, employee);

        history.push("/employees");
    }
    let name = employeeState.firstName;
    console.log(name);
    return (
        <Container maxWidth={2440}>

            <HeaderComponent title="Edit Employee"></HeaderComponent>
            <Container align="center">
            <form>
                <div className = "from-group">
                    <label> First Name: </label>
                    <input placeholder="First Name" name="firstName" className="form-control"
                        value={firstName} onChange={e => setFirstName(e.target.value)}/>
                </div>
                <div className = "from-group">
                    <label> Last Name: </label>
                    <input placeholder="Last Name" name="lastName" className="form-control"
                        value={secondName} onChange={e => setSecondName(e.target.value)}/>
                </div>
                <div className = "from-group" style={{marginBottom: "10px"}}>
                    <label> Email Adress: </label>
                    <input placeholder="Email Adress: " name="emailId" className="form-control"
                        value={salary} onChange={e => setSalary(e.target.value)}/>
                </div>


            </form>
            <form className={classes.root} noValidate autoComplete="off" >
            <div style={{marginTop:"20px"}}>
            <TextField id="standard-basic" label="First name"
             value={toString(name)}
             onChange={e => setFirstName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Second name" 
            defaultValue={employeeState.secondName}
            onChange={e => setSecondName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Salary" 
            defaultValue={employeeState.salary}
            onChange={e => setSalary(e.target.value)}/>
            </div>
            <Button variant="contained" color="primary" style={{marginLeft:"10px"}}
            onClick={acceptClick}
            >
            Accept
            </Button>
            </form>
            </Container>
        </Container>


    );
}

export default EditEmployee;
