import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import React, {useState} from 'react'
import axios from 'axios';
import HeaderComponent from '../HeaderComponent';
import { useHistory, useLocation } from 'react-router-dom';
import {location} from 'react-router-dom'
const EMPLOYEE_API_BASE_URL = "http://localhost:8080/api/v1/employee";
const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));
function AddEmployee(props) {
    const history = useHistory();
    const classes = useStyles();
    const [employeeState, setEmployeeState] = useState({});
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [salary, setSalary] = useState('');
    const location = useLocation();
    const getEmployeeById=(id)=>{
        axios.get(EMPLOYEE_API_BASE_URL+'/'+id).then(data=>{
            let employee = data.data;
            setEmployeeState({
              id: employee.id,
              firstName:employee.firstName,
              secondName:employee.secondName,
              salary:employee.salary  
            });
        })

    }
    React.useEffect(() => {
        let id = props.match.params.id;
        getEmployeeById(id);
      }, []);
    const acceptClick=()=>{
        const employee = {
            firstName: employeeState.firstName,
            secondName: employeeState.secondName,
            salary: employeeState.salary
        }
        console.log(employee);
        axios.post(EMPLOYEE_API_BASE_URL, employee);
        props.getEmployee();
    }

    return (
        <div className="container" align="center" style={{margin:"10px"}}>
            <HeaderComponent title="">

            </HeaderComponent>
            <form className={classes.root} noValidate autoComplete="off" >
            <div>
            <TextField id="standard-basic" label="First name"
             defaultValue={employeeState.firstName}
             onChange={e => setFirstName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Second name" 
            defaultValue={employeeState.secondName}
            onChange={e => setSecondName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Salary" 
            defaultValue={employeeState.salary}
            onChange={e => setSalary(e.target.value)}/>
            </div>
            <Button variant="contained" color="primary" style={{marginLeft:"10px"}}
            onClick={acceptClick}
            >
            Accept
            </Button>
            </form>


        </div>


    );
}

export default AddEmployee;
