import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import React, {useState} from 'react'
import axios from 'axios';
import { useHistory } from 'react-router-dom';
const EMPLOYEE_API_BASE_URL = "http://localhost:8080/api/v1/employee";
const useStyles = makeStyles((theme) => ({
    root: {
      '& > *': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }));
function AddEmployee() {
    const history = useHistory();
    const classes = useStyles();
    const [firstName, setFirstName] = useState('');
    const [secondName, setSecondName] = useState('');
    const [salary, setSalary] = useState('');
    const acceptClick=()=>{
        const employee = {
            firstName: firstName,
            secondName: secondName,
            salary: salary
        }
        console.log(employee);
        axios.post(EMPLOYEE_API_BASE_URL, employee);
        history.push("/employees");
    }
    const cancelClick=()=>{
        history.push("/employees");
    }
    return (
        <div className="container" align="left" style={{padding}}>
            <h2>Add employee</h2>
            <form className={classes.root} noValidate autoComplete="off" >
            <div>
            <TextField id="standard-basic" label="First name"
             onChange={e => setFirstName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Second name" 
            onChange={e => setSecondName(e.target.value)}/>
            </div>
            <div>
            <TextField id="standard-basic" label="Salary" 
            onChange={e => setSalary(e.target.value)}/>
            </div>

            </form>
            <Button variant="contained" color="primary" style={{margin:"5px"}}
            onClick={acceptClick}>
            Accept
            </Button>
            <Button variant="contained" color="secondary" style={{margin:"5px"}}
            onClick={cancelClick}>
            Cancel
            </Button>
        </div>


    );
}

export default AddEmployee;
